import React, { useState, useEffect } from 'react';
import { accountService } from '@/_services';
import { Row, Col } from 'react-bootstrap';
// import { Form, Button, FormControl } from 'react-bootstrap';

function Searchbar() {
  const [user, setUser] = useState({});
  const search = {
    width: '18px',
  };
  const searchSpacing = {
    margin: '0 0 0px -10px',
  };

  useEffect(() => {
    const subscription = accountService.user.subscribe(x => setUser(x));
    return accountService.user.unsubscribe;
  }, []);

  // only show nav when logged in
  if (!user) return null;

  return (
    <Row>
      <Col sm={6}>
        <form>
          <div class="wrapper">
            <div style={searchSpacing}>
              <img class="search-icon" style={{width: '20px'}} src="data:image/svg+xml;utf8;base64," />
            </div>
            <div style={{padding:'px 0 0 0', width: '100%'}}>
              <input 
                style={{padding:'0 0 10px 40px', height: '100%'}}
                class="no-outline" 
                placeholder="Search"
                type="text"
                class="search" 
              />
            </div>
          </div>
        </form>
      </Col>
    </Row>
  );
}
export { Searchbar };
